package week2.day2;

public class PrintEvenNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//0 to 25
		//for iteration use for loop
		//if condition
		int even = 25;
		for (int i = 0; i < even; i++) {
			if(i%2!=1)
			{
				System.out.println("Even Number is:"+i);
			}
			
		}

	}

}
