package week2.day2;

public class PrintOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//0 to 25
		//for iteration use for loop
		//if condition
		int odd = 25;
		for (int i = 0; i< odd; i++) {
			if(i%2==1)
			{
				System.out.println("Odd number is "+ i);
			}
			
		}
	}

}
